var map = L.map("map").setView([-7.770398086343634, 110.37753582000731], 17);
L.tileLayer("http://{s}.tile.osm.org/{z}/{x}/{y}.png", {
  attribution:
    '&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors',
}).addTo(map);

var editableLayers = new L.FeatureGroup();
map.addLayer(editableLayers);
var drawControl = new L.Control.Draw({
  edit: {
    featureGroup: editableLayers,
  },
});
map.addControl(drawControl);
map.on(L.Draw.Event.CREATED, function (event) {
  editableLayers.addLayer(event.layer);
});

//save mission to data base
function saveToDatabase() {
  let namaMisi = prompt("nama misi:");
  $.post("/misi", {
    namaMisi: namaMisi,
    geoJSON: JSON.stringify(editableLayers.toGeoJSON()),
  });
}
